import csvdef escrever_csv(nome_arquivo, dados):    try:        with open(nome_arquivo, 'w') as arquivo_csv:            escritor = csv.writer(arquivo_csv)            escritor.writerow(['Nome', 'Idade', 'Cidade'])            for linha in dados:                escritor.writerow(linha)        print(f"Arquivo salvo com sucesso em {nome_arquivo}")    except Exception as e:        print(f"Erro {e} ao salvar o arquivo.")dados = [    ['Ana', 28, 'Rio de Janeiro'],    ['Bruno', 35, 'SÃ£o Paulo'],    ['Carla', 22, 'Belo Horizonte'],    ['Daniel', 41, 'Salvador'],    ['Fernanda', 30, 'Curitiba'],    ['Lucas', 19, 'Fortaleza'],    ['Mariana', 26, 'Recife'],    ['Rafael', 38, 'Porto Alegre']]if __name__ == "__main__":    nome_arquivo = input("Digite o nome do arquivo csv: ").strip()    escrever_csv(nome_arquivo, dados)